mapper.select:
SELECT 

FROM t_cost_c a

WHERE a.id = #{id}

SELECT
FROM t_cost_c a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.gxdm LIKE '%'||#{university.gxdm}||'%' '%'+#{university.gxdm}+'%' concat('%',#{university.gxdm},'%')
AND a.zydm LIKE '%'||#{specialityManage.zydm}||'%' '%'+#{specialityManage.zydm}+'%' concat('%',#{specialityManage.zydm},'%')
AND a.psfy = #{psfy}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_cost_c a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_cost_c SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
